INPUT_STRING = ""

start_index = 0
end_sub_index = 14

non_repeated_characters = False



while non_repeated_characters != True:
    current_substring = INPUT_STRING[start_index:end_sub_index]
    current_characters = []
    for character in current_substring:
        if character not in current_characters:
            current_characters.append(character)
    if len(current_characters) == 14:
        non_repeated_characters = True
    else:
        start_index += 1
        end_sub_index += 1

def find_message(input_string):
    target_unique_characters = 14
    end_index = 14
    start_index = 0
    while end_index < len(input_string):
        unique_characters = set(input_string[start_index:end_index])
        if len(unique_characters) == target_unique_characters:
            return end_index
        start_index += 1
        end_index += 1

print(find_message(INPUT_STRING))